CREATE TABLE IF NOT EXISTS `ims_xcommunity_activity` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL, `title` varchar(50) NOT NULL, `regionid` text NOT NULL, `starttime` int(11) NOT NULL, `endtime` int(11) NOT NULL, `enddate` varchar(30) NOT NULL, `picurl` varchar(1000) NOT NULL, `number` int(11) NOT NULL DEFAULT '1', `content` varchar(2000) NOT NULL, `status` int(1) NOT NULL COMMENT '1置顶', `price` decimal(12,2) NOT NULL, `createtime` int(11) unsigned NOT NULL, `resnumber` int(11) unsigned NOT NULL COMMENT '报名人数', `uid` int(10) unsigned NOT NULL, `province` varchar(50) DEFAULT NULL, `city` varchar(50) DEFAULT NULL, `dist` varchar(50) DEFAULT NULL, `uniacid` int(10) DEFAULT NULL, `num` int(11) DEFAULT '0', `allregion` int(1) DEFAULT NULL, `recommand` tinyint(1) DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='小区活动表';
CREATE TABLE IF NOT EXISTS `ims_xcommunity_activity_region` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `activityid` int(10) unsigned NOT NULL, `regionid` int(11) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=42 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_xcommunity_alipayment` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) NOT NULL COMMENT '公众号ID', `pid` int(11) NOT NULL, `account` varchar(50) NOT NULL COMMENT '支付宝账号', `partner` varchar(50) NOT NULL COMMENT '合作者身份', `secret` varchar(50) NOT NULL COMMENT '校验密钥', `type` int(1) DEFAULT NULL, `uid` int(11) DEFAULT NULL, `userid` int(11) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='独立支付宝配置';
CREATE TABLE IF NOT EXISTS `ims_xcommunity_announcement` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL COMMENT '公众号ID', `regionid` int(11) DEFAULT NULL, `pid` int(10) unsigned NOT NULL COMMENT '物业ID', `title` varchar(255) NOT NULL COMMENT '标题', `content` text NOT NULL COMMENT '内容', `author` varchar(50) NOT NULL COMMENT '作者', `createtime` int(10) unsigned NOT NULL, `starttime` int(11) unsigned NOT NULL COMMENT '开始时间', `endtime` int(11) unsigned NOT NULL COMMENT '结束时间', `status` tinyint(1) NOT NULL COMMENT '状态 1禁用，2启用', `enable` tinyint(1) NOT NULL COMMENT '模板类型', `datetime` varchar(100) NOT NULL, `location` varchar(100) NOT NULL COMMENT '通知范围', `reason` text, `remark` varchar(100) NOT NULL COMMENT '通知备注', `uid` int(11) NOT NULL, `province` varchar(50) DEFAULT NULL, `city` varchar(50) DEFAULT NULL, `dist` varchar(50) DEFAULT NULL, `uniacid` int(10) DEFAULT NULL, `pic` varchar(100) DEFAULT NULL, `type` int(1) NOT NULL, `allregion` int(1) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=226 DEFAULT CHARSET=utf8 COMMENT='公告管理';
